mapper.select:
select DATE_FORMAT( p.create_time, '%Y-%m-%d' ) AS time,
       IFNULL(sum(p.energy),0) AS energy,
       IFNULL(a.area,'') AS area
from dhlk_light_led_power p
left join dhlk_light_led   l on p.led_sn=l.sn
left join dhlk_light_area a on a.id=l.area_id
where l.tenant_id=#{tenantId}
group by time,a.area
having 1 = 1

     and time >= #{startDate}


     and time <= #{endDate}


    and area = #{area}

order by time desc

select (@rownumber :=@rownumber + 1) AS line, time, area, energy from (select DATE_FORMAT( p.create_time, '%Y-%m-%d' ) AS time, IFNULL(a.area,'') AS area, IFNULL(sum(p.energy),0) AS energy from dhlk_light_led_power p left join dhlk_light_led l on p.led_sn=l.sn left join dhlk_light_area a on a.id=l.area_id, (SELECT (@rowNum := 0)) b where l.tenant_id=#{tenantId} group by time,a.area having 1 = 1
and time >= #{startDate}
and time <= #{endDate}
and area = #{area}
order by time desc) power, (SELECT @rownumber := 0) r;
