mapper.select:
select count(0) count from dhlk_basic_tenant t where t.status=0
and substring_index(adress,',',1)=#{province}
union all select count(0) count from dhlk_light_switch s left join dhlk_basic_tenant t on t.id=s.tenant_id where t.status=0
and substring_index(adress,',',1)=#{province}
union all select count(0) count from dhlk_light_led l left join dhlk_basic_tenant t on t.id=l.tenant_id where t.status=0
and substring_index(adress,',',1)=#{province}

select  t.id,t.name
from dhlk_basic_tenant t where t.status=0

    and substring_index(adress,',',1)=#{province}

order by create_time desc limit ${limit}

select id from dhlk_basic_tenant t where 1=1
and substring_index(t.adress,',',1) = #{province}
and t.status=0

SELECT IFNULL(SUM( lp.energy ),0) FROM dhlk_light_led_power lp
where tenant_id in
#{item}
)
